import streamlit as st
import pandas as pd
import seaborn as sns

st.title("Who Are Palmer's Penguins?")
st.write("Use this app to learn more about Palmer's Penguins: who they are and where they came from. Penguins are one of the most interesting animals and as you interact with this platform, I hope you are intruiged by how the statistical information demonstrates their demographic.")

df = pd.read_csv("data/penguins.csv")

penguin_appretiation = st.select_slider("How much do you like penguins?",["I hate them!","I know literally nothing about penguins", "God's greatest gift to Earth"])
if penguin_appretiation == "I hate them!":
    st.write("What! How could you not like penguins?")
    st.text_input("Please explain your hatred")
elif penguin_appretiation == "I know literally nothing about penguins":
    st.write("No worries! We can give you lots of information")
    st.radio("Which of these do you want to learn about first?", ["Species", "Island", "Physical Features"])
else:
    st.write("I totally agree!")
    st.number_input("How many penguins have you adopted through World Wildlife?", min_value=0, step=1)

st.write("Let's learn by species! Choose one which interests you to learn more about where they come from and what they look like. To sort by a specific feature, simply click on the metric!")
species = st.selectbox("Select a species", df["species"].unique())
species_df = df[df["species"] == species]
st.write(f"Penuins of {species} species:")
st.dataframe(species_df)
if species == 'Adelie':
    st.image('data:image/jpeg;base64,')
    st.caption('This is an Adelie penguin')
elif species == 'Gentoo':
    st.image('data:image/jpeg;base64,')
    st.caption('This is a Gentoo penguin')
elif species == 'Chinstrap':
    st.image('data:image/jpeg;base64,')
    st.caption('This is a Chinstrap penguin')

st.write("Now let's learn by year! How old are the penguins?")
year = st.select_slider("Choose a year!", df['year'].unique())
year_df = df[df['year'] == year]
age = 2025-year
st.write(f"The penguins from {year} would be {age} years old today!")
st.dataframe(year_df)

st.write("Now that you know so much about penguins, I'm sure you love them. Look how cute they are!")
st.image('https://upload.wikimedia.org/wikipedia/commons/thumb/a/a3/Aptenodytes_forsteri_-Snow_Hill_Island%2C_Antarctica_-adults_and_juvenile-8.jpg/800px-Aptenodytes_forsteri_-Snow_Hill_Island%2C_Antarctica_-adults_and_juvenile-8.jpg',width=300)

st.write("I'm sure now you're interested in adopting your own penguin! Check out [World WildLife](https://gifts.worldwildlife.org/gift-center/gifts/species-adoptions/gentoo-penguin) and adopt your own Gentoo Penguin today!")